(function($){$.fn.znCrop=function(b){var c=$.extend({firstLevel:true},b);(c.firstLevel?this.children('img'):this.find('img')).each(function(){var a=this.naturalWidth||$(this).css('width','auto').width(),naturalHeight=this.naturalHeight||$(this).css('height','auto').height();$(this).parent().css('overflow','hidden');if(a>=naturalHeight){$(this).css('height','100%').css('width',null)}else{$(this).css('width','100%').css('height',null)}});return this}}(jQuery));
